export default App;

args.content:
import React from 'react';

const ChatWindow: React.FC = () => {
  return (
    <div className="flex flex-col flex-grow bg-background rounded-lg shadow-md overflow-hidden">
      {/* Message history area */}
      <div className="flex-grow p-4 overflow-y-auto">
        {/* Welcome message */}
        <div className="mb-4 text-center text-muted-foreground">
          Welcome to Himolde Study Friend! How can I help you today?
        </div>
        {/* Chat messages will go here */}
      </div>

      {/* Input field and send button */}
      <div className="border-t p-4 flex items-center gap-2">
        {/* Input component */}
        <input
          type="text"
          placeholder="Type your message..."
          className="flex-grow p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
        />
        {/* Send button */}
        <button className="bg-primary text-primary-foreground px-4 py-2 rounded-lg hover:bg-primary/90">
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatWindow;

