import { render, screen } from '@testing-library/react';
import { describe, it, expect } from 'vitest';
import ChatWindow from './ChatWindow';

// Extend Vitest with DOM-specific assertions from @testing-library/jest-dom
import '@testing-library/jest-dom';

describe('ChatWindow', () => {
  it('renders correctly with message history area, input field, and send button', () => {
    render(<ChatWindow />);

    // Check if the chat window is displayed (AC 3)
    const chatWindow = screen.getByRole('complementary', { name: /chat window/i }); // Using a more specific role/name for accessibility
    expect(chatWindow).toBeInTheDocument();

    // Check for the message history area (AC 4)
    const messageHistoryArea = screen.getByRole('region', { name: /message history/i }); // Using role for scroll-area
    expect(messageHistoryArea).toBeInTheDocument();

    // Check for the welcome message (AC 4)
    expect(screen.getByText(/welcome to himolde study friend/i)).toBeInTheDocument();

    // Check for the text input field (AC 5)
    expect(screen.getByPlaceholderText(/type your message/i)).toBeInTheDocument();

    // Check for the "Send" button (AC 6)
    expect(screen.getByRole('button', { name: /send/i })).toBeInTheDocument();
  });
});

